mapper.update:
UPDATE BOARD SET
	B_subjcet = #{b_subjcet},
	B_content= #{b_content:CLOB},
	mod_id = #{reg_id},
	mod_dt= TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'),
	fcategori = #{fcategori}
WHERE B_code= #{b_code}
AND B_seq = #{b_seq}

UPDATE BOARD SET
	B_del_YN = 'Y',
	mod_id = #{reg_id},
	mod_dt= TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
WHERE B_code= #{b_code}
AND B_seq = #{b_seq}

UPDATE BOARD SET
	B_readnum = B_readnum+1
WHERE B_code= #{b_code}
AND B_seq = #{b_seq}

UPDATE BOARD SET
	B_heartnum =B_heartnum+1
WHERE B_code= #{b_code}
AND B_seq = #{b_seq}
